import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';

import { useParams } from 'react-router-dom';
import {useEffect, useState} from "react";

function Block(){
    const http = dataM.http
    let num=useParams(num);
    let gungNum = Number(num.num)+1;
    let [elDiv, setelDiv] = useState();

useEffect (()=>{
    let div='';
    for(let v of dataD.list){
        if(v.gung_number == gungNum){
            div+=`<div className='contents-contain'>
                <p><img src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                <span>${v.contents_kor}</span>
            </div>`;
        }
    };
    setelDiv(div)
},[num]);

    return(
        <>
            <div className='contents-wrap' dangerouslySetInnerHTML={ {__html: elDiv} }>
            </div>
        </>
    );
}
export default Block;
