import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';
import List from './List';
import list_black_24dp from '../img/list_black_24dp.svg';
import dashboard_black_24dp from '../img/dashboard_black_24dp.svg';

import { useParams } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";
import { useSelector } from 'react-redux';


function Gung(){
    const elList = useRef([]);
    const elShow = useRef([]);

    let {num} = useParams();
    let [select, setSelect] = useState(0);

    function show(e){
        console.log(e)
        if(e.target==elShow.current.children[0]){
            elList.current.children[0].classList.add('active');
            elList.current.children[1].classList.remove('active');
        }else{
            elList.current.children[0].classList.remove('active');
            elList.current.children[1].classList.add('active');
        }
    }

    function change(e){
        setSelect(Number(e.target.value));
    }
    return(
        <>
            <div className='mwidth gung-title'>
                <h2>{dataM.gogung[num].gung_Name}</h2>
                <div>
                    <p>
                        <p onClick={show}><img src={dashboard_black_24dp} alt="갤러리 보기"/></p>
                        <p onClick={show}><img src={list_black_24dp} alt="리스트 보기"/></p>
                    </p>
                    <select onChange={change}>
                        <option value={0}>오름차순</option>
                        <option value={1}>내림차순</option>
                        <option value={2}>인기순</option>
                    </select>
                </div>
            </div>
            <div className="mwidth" ref={elList}>
                <List sort={select}/>
            </div>

        </>
    );
}
export default Gung;
