import {Link} from 'react-router-dom';
import {useRef, useState} from "react";

function Header(){
    const elNav = useRef();
    const elBuger = useRef();
    let [num,setNum] = useState();

    // burger menu
    function navigation(){
        elNav.current.classList.toggle('active');
        elBuger.current.classList.toggle('active');
    }
    function menuClose(){
        elNav.current.classList.remove('active');
        elBuger.current.classList.remove('active');
    }
    // 메뉴 클릭시 키값 받기
    function getNum(n){
        setNum(n);
    }

    return(
        <>
            <header>
                <div className='mwidth head'>
                    <Link to="/"><h1>고궁</h1></Link>
                    <div className='h-left'>
                        <Link to="/search"><img src='' alt='search'/></Link>
                        <p className='bugermenu' ref={elBuger} onClick={navigation}>
                            <span></span>
                            <span></span>
                            <span></span>
                        </p>
                    </div>
                </div>
                <nav className='menu'ref={elNav}>
                    <div className='menu-contents'>
                        <ul>
                            <li onClick={menuClose,getNum}><Link to="/gung">경복궁</Link></li>
                            <li onClick={menuClose,getNum}><Link to="/gung">창경궁</Link></li>
                            <li onClick={menuClose,getNum}><Link to="/gung">창덕궁</Link></li>
                            <li onClick={menuClose,getNum}><Link to="/gung">덕수궁</Link></li>
                            <li onClick={menuClose,getNum}><Link to="/gung">종묘</Link></li>
                        </ul>
                        <ul>
                            <li onClick={menuClose}><Link to="/login">로그인</Link></li>
                            <li onClick={menuClose}><Link to="/jogin">회원가입</Link></li>
                            <li onClick={menuClose}><Link to="/like">좋아요</Link></li>
                        </ul>
                    </div>
                </nav>
            </header> 
        </>
    );
}
export default Header;
