import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';
import List from './List';

import { useParams } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";


function Gung(){
    const elList = useRef([]);
    const elShow = useRef([]);
    let {num} = useParams();

    function show(e){
        if(e.target=elShow.current.children[0]){
            elList.current.children[0].classList.toggle('active');
        }else{
            elList.current.children[1].classList.toggle('active');
        }
        console.log(elList.current.children[0])
    }

    return(
        <>
            <div>
                <h2>{dataM.gogung[num].gung_Name}</h2>
                <div>
                    <p ref={elShow} onClick={show}>
                        <span onClick={show}>갤러리보기</span>
                        <span>목록보기</span>
                    </p>
                    <select>
                        <option>오름차순</option>
                        <option>내림차순</option>
                        <option>인기순</option>
                    </select>
                </div>
            </div>
            <div ref={elList}>
                <List/>
            </div>

        </>
    );
}
export default Gung;
