import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';

import { useParams, useNavigate } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";

function List({sort}){
    const http = dataM.http;
    const elBlock = useRef([]);
    const elList = useRef([]);

    let navigate = useNavigate();
    let num=useParams();
    let gungNum = Number(num.num)+1;

    let [elDivblock, setelDivblock] = useState();
    let [elDivlist, setelDivlist] = useState();
    let [select, setSelect] = useState({sort});

    // 상세페이지 연동
    function moveDetail(e){
        let dNum = e.target.dataset.s;
        e.target = navigate(`/listdetail/${dNum}`);
    }
    //목록 정렬
    if(select==0){
            dataD.list.sort(function(a,b){
                if(a.contents_kor > b.contents_kor){
                    return 1;
                }
                if(a.contents_kor < b.contents_kor){
                    return -1;
                }
                return 0;
            })
        }else if(select==1){
            dataD.list.sort(function(a,b){
                if(a.contents_kor < b.contents_kor){
                    return 1;
                }
                if(a.contents_kor > b.contents_kor){
                    return -1;
                }
                return 0;
            })
    }


    // 궁번호에 따른 데이터 리스트 필터
    useEffect (()=>{
        let block='';
        let list='';
        for(let v of dataD.list){
            if(v.gung_number == gungNum){
                block+=`<div data-s=${v.serial_number} className='list-contents-contain'>
                    <p><img data-s=${v.serial_number} src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                </div>`;
                list+=`<div data-s=${v.serial_number} className='list-contents-contain'>
                        <p><img data-s=${v.serial_number} src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                        <span>${v.contents_kor}</span>
                </div>`;
            }
        };
        setelDivblock(block)
        setelDivlist(list)
    },[num]);
// 각 목록 선택시 상세페이지 이동?

    return(
        <>
            <div ref={elBlock} onClick={moveDetail} className='block-contents-wrap active' dangerouslySetInnerHTML={ {__html: elDivblock} }>
            </div>
            <div ref={elList} onClick={moveDetail} className='list-contents-wrap' dangerouslySetInnerHTML={ {__html: elDivlist} }>
            </div>
        </>
    );
}
export default List;
