import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';
import List from './List';

import { useParams } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";


function Gung(){
    const elList = useRef();
    let {num} = useParams();
    const elShow = useRef([]);
    const http = dataM.http
    let gungNum = Number(num.num)+1;
    let [elDiv, setelDiv] = useState();

    function show(e){
        if(e.target=elShow.current.children[0]){
            elList.classList.toggle('active');
        }
    }


    useEffect (()=>{
        let div='';
        for(let v of dataD.list){
            if(v.gung_number == gungNum){
                div+=`<div className='list-contents-contain'>
                    <p><img src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                    <span>${v.contents_kor}</span>
                </div>`;
            }
        };
        setelDiv(div)
    },[num]);

    return(
        <>
            <div>
                <h2>{dataM.gogung[num].gung_Name}</h2>
                <div>
                    <p ref={elShow} onClick={show}>
                        <span onClick={show}>갤러리보기</span>
                        <span>목록보기</span>
                    </p>
                    <select>
                        <option>오름차순</option>
                        <option>내림차순</option>
                        <option>인기순</option>
                    </select>
                </div>
            </div>
            <div>
                <div className='mwidth block-contents-wrap active' dangerouslySetInnerHTML={ {__html: elDiv} }>
                </div>
                <div className='mwidth list-contents-wrap' dangerouslySetInnerHTML={ {__html: elDiv} }>
                </div>
            </div>

        </>
    );
}
export default Gung;
