import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';

import { useParams } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";

function Block(){
    const http = dataM.http;
    let num=useParams();
    let gungNum = num.num+1;
    let elDiv = useRef([]);

    useEffect(()=>{
        if(elDiv.current.dataset.num != gungNum){
            elDiv.current.style = 'display:none';
        }
    },[])

    return(
        <>
            <div className='contents-wrap'>
                {dataD.list.map((v,k)=>(
                    <div key={k} ref={elDiv} data-num={v.gung_number} className='contents-contain'>
                        <p><img src={http+v.imgUrl} alt=""/></p>
                        <span>{v.contents_kor}</span>
                    </div>))
                }
            </div>
        </>
    );
}
export default Block;
