import dataM from '../data/gogungCategory.json';
import dataD from '../data/gogungListOpenApi.json';

import { useParams, useNavigate } from 'react-router-dom';
import {useRef, useEffect, useState} from "react";

function List({}){
    const http = dataM.http;
    const elList = useRef([]);
    let num=useParams();
    let gungNum = Number(num.num)+1;

    let [elDivblock, setelDivblock] = useState();
    let [elDivlist, setelDivlist] = useState();

    function moveDetail(e){
        console.log(e.target.current)
    }

    // 궁번호에 따른 데이터 리스트 필터
    useEffect (()=>{
        let block='';
        let list='';
        for(let v of dataD.list){
            if(v.gung_number == gungNum){
                block+=`<div className='list-contents-contain'>
                    <p><img src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                </div>`;
                list+=`<div className='list-contents-contain'>
                        <p><img src=${http+v.imgUrl} alt="출처:문화재청"/></p>
                        <span>${v.contents_kor}</span>
                </div>`;
            }
        };
        setelDivblock(block)
        setelDivlist(list)
    },[num]);
// 각 목록 선택시 상세페이지 이동?

    return(
        <>
            <div className='block-contents-wrap active' dangerouslySetInnerHTML={ {__html: elDivblock} }>
            </div>
            <div ref={elList} onClick={moveDetail} className='list-contents-wrap' dangerouslySetInnerHTML={ {__html: elDivlist} }>
            </div>
        </>
    );
}
export default List;
